import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'desafio_01';
}
<div class="grid-container">
  <app-menu class="grid-menu"></app-menu>
  <app-tool-bar class="grid-toolbar"></app-tool-bar>
  <app-content-area class="grid-content"></app-content-area>
</div>
